typedef __int128 int128_t;
typedef unsigned __int128 uint128_t;
#include "zeosio.hpp"
#include <iostream>

int main()
{
    char str[] = "";
    std::vector<bool> v;
    for(char* c = str; *c != '\0'; c++)
    {
        v.push_back(*c == '0' ? false : true);
    }

    std::vector<zeos::groth16::Scalar> inputs = zeos::groth16::compute_multipacking(v);

    
    std::cout << zeos::groth16::inputs_hexstr(inputs) << std::endl;
}